const noJs=document.querySelectorAll(".no-js"),portfolioItem=document.querySelectorAll(".portfolio-items");let fullImg=0,itemActive="";function classRemove(){noJs.forEach(e=>{e.classList.remove("no-js")})}window.addEventListener("load",classRemove()),window.addEventListener("resize",classRemove()),portfolioItem.forEach(e=>{let t=e.getAttribute("id");e.addEventListener("click",()=>{0==fullImg?(document.querySelector("#"+t).classList.add("port-active"),fullImg=1,itemActive=t):1==fullImg&&(t===itemActive?(document.querySelector("#"+t).classList.remove("port-active"),fullImg=0):(document.querySelector("#"+itemActive).classList.remove("port-active"),document.querySelector("#"+t).classList.add("port-active"),itemActive=t))}),e.addEventListener("mousemove",()=>{e.classList.add("port-active")}),e.addEventListener("mouseout",()=>{e.classList.remove("port-active")})});